async () => {
  var input = FilePicker1.files[0];
  var n = input.data.length;
  console.log(n);
  var arr = [];
  for (var i = 0; i < n; i++) {
    arr.push(input.data[i]["customer_ids"]);
    if (i % 1000 == 999) {
      console.log(arr);
      storeValue("customer_ids_batch", arr);
      await Api1.run();
      arr = [];
    }
  }
  if (arr.length > 0) {
    storeValue("customer_ids_batch", arr);
    await Api1.run();
  }
  showAlert("Customer uploaded successfully");
}

