actionConfiguration.body:
async () => {
  Progress1.isVisible = true;
  var input = FilePicker1.files[0];
  var n = input.data.length;
  var arr = [];
  for (var i = 0; i < n; i++) {
    arr.push(input.data[i]["customer_ids"]);
    if (i % 10000 == 9999) {
      storeValue("customer_ids_batch", arr, false);
      Api1.run();
      arr = [];
      Progress1.progress += 1;
    }
  }
  storeValue("customer_ids_batch", arr);
  if (arr.length > 0) {
    Api1.run();
  }
  Progress1.isVisible = false;
  showAlert("Customer uploaded successfully");
}

